        // 리엑트에선 DOM으로 접근하지 않고 리엑트만의 언어인 Ref로 접근함 
    };
    render() {
        return (
            <form className='add-form' onSubmit={this.onSubmit}>
                <input
                    ref={inputRef}
                    type="text" 
                    className='add-input' 
                    placeholder='Habit' 
                />
